function snd.luminary_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "angel aura me" end elseif snd.toggles.lightning then battack = "evoke lightning "..snd.bashing.target else battack = "smite "..snd.bashing.target..snd.sep.."angel power" end end
function snd.monk_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) --ensure battack is cleared going into this. battack = ""
if mp >= 30 and hp <= 75 and snd.transmutation and hasSkill("Transmute") then battack = battack.."transmute mana 1000"..snd.sep tempTimer(2,[[snd.transmutation = true]]) end
if not snd.balance.numbness and hasSkill("Numbness") then if hp <= 70 then battack = battack.."numb"..snd.sep end end
if (hp <=40 or snd.have_aff("blackout")) and snd.shield_check() then if snd.punisher then else battack = battack..snd.sep.."touch shield" end elseif snd.toggles.cobra and hasSkill("Cobra") then if snd.stance ~= "cobra" then battack = battack.."cbs" else battack = battack.."combo "..snd.bashing.target.." sdk sdk" end else battack = battack.."combo "..snd.bashing.target.." sdk ucp ucp" end end
function snd.oneiromancer_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif hp <= 55 and hasSkill("Flower") then battack = "unfurl flower at me" elseif snd.toggles.newbie then battack = "starlight "..snd.bashing.target else if snd.wielded.left.name == "athame" or snd.wielded.right.name == "athame" then battack = "impel athame at "..snd.bashing.target else battack = "starlight "..snd.bashing.target end end end
function snd.praenomen_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp) bp = tonumber(gmcp.Char.Vitals.blood)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif hp <= 45 and bp >= 30 and snd.balance.mend and hasSkill("Mending") then battack = "mend" elseif bp >= 50 and hp <= 30 and hasSkill("Deluge") then if snd.balance.unbroken and snd.defenses.def_deluge.state == "down" then battack = "blood deluge"..snd.sep.."frenzy "..snd.bashing.target else battack = "frenzy "..snd.bashing.target end elseif bp <= 25 then battack = "feed corpse" else battack = "frenzy "..snd.bashing.target end end
function snd.predator_bash() hp = 100 * (gmcp.Char.Vitals.hp / gmcp.Char.Vitals.maxhp) mp = 100 * (gmcp.Char.Vitals.mp / gmcp.Char.Vitals.maxmp) battack = "" local stance = gmcp.Char.Vitals.knife_stance or "None" local attacks = snd.get_predator_bash_attacks() if hp <= 40 and snd.shield_check() then battack = "touch shield" else battack = attacks[stance].." "..snd.bashing.target end end
function snd.ravager_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp) battack = ""
if tonumber(gmcp.Char.Vitals.bleeding) > 1000 and snd.cauterize and mp >= 15 and hasSkill("Prolong") then battack = "invoke prolong me"..snd.sep snd.cauterize = false tempTimer(2,[[snd.cauterize = true]]) end
if hp <=40 and snd.shield_check() then if snd.punisher then if hasSkill("Seethe") then battack = battack.."seethe" end else battack = battack.."touch shield" end elseif not snd.can_arms() and hasSkill("Concussion") then battack = battack.."concuss "..snd.bashing.target else battack = battack.."onslaught "..snd.bashing.target.." bully bully" end
end
function snd.revenant_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp) endur = 100*(gmcp.Char.Vitals.ep/gmcp.Char.Vitals.maxep)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif snd.toggles.newbie then battack = "rivebash "..snd.bashing.target elseif (snd.wielded.left.name:find("warhammer") or snd.wielded.left.name:find("bastard") or snd.wielded.left.name:find("bardiche")) and hasSkill("Fell") then battack = "fell "..snd.bashing.target else battack = "dpl "..snd.bashing.target end if (hp >= 61 and wp >= 50 and hasSkill("Duskosa")) and not snd.toggles.newbie then battack = battack.. " duskosa duskosa" elseif not snd.toggles.newbie and hasSkill("Annul") then battack = "annul left"..snd.sep.."annul right"..snd.sep..battack end end
function snd.runecarver_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp)
if hp <=40 and snd.shield_check() then if snd.punisher then if snd.reflection_charge > 0 and snd.defenses.def_reflection.state ~= "deffed" then battack = "runecarve branch" end else battack = "touch shield" end elseif snd.defenses.def_reflection.state == "deffed" and hp <= 60 then -- let the reflection do its job elseif hp <= 60 and snd.reflection_charge > 0 then battack = "runecarve branch" elseif snd.toggles.newbie then battack = "hex enervate "..snd.bashing.target else if tonumber(gmcp.Char.Vitals.spiritpact) < 2 and hasSkill("Spiritpact") then battack = "spiritpact despair " ..snd.bashing.target..snd.sep.."hex malady "..snd.bashing.target else battack = "hex malady " ..snd.bashing.target end end end
function snd.sciomancer_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp)
if hp <=40 and snd.shield_check() then if snd.punisher then if snd.reflection_charge > 0 and snd.defenses.def_reflection.state ~= "deffed" then battack = "cast reflection" end else battack = "touch shield" end elseif snd.defenses.def_reflection.state == "deffed" and hp <= 60 then -- let the reflection do its job elseif hp <= 60 and snd.reflection_charge > 0 then battack = "cast reflection" elseif snd.toggles.newbie then battack = battack..snd.sep.."cast ruin "..snd.bashing.target else if hasSkill("Shadowprice") and tonumber(gmcp.Char.Vitals.shadowprice) < 2 then battack = "shadowprice gloom " ..snd.bashing.target..snd.sep.."cast fever "..snd.bashing.target else battack = "cast fever " ..snd.bashing.target end end end
function snd.sentinel_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif snd.toggles.newbie then battack = "dhuriv combo "..snd.bashing.target.." slash stab" else battack = "dhuriv combo "..snd.bashing.target.." crosscut thrust" end end
function snd.shaman_bash()
hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp) battack = "" if snd.familiar and snd.balance.morph and hasSkill("Morph") then if snd.shamanpet == "bear" then battack = "order spirit passive"..snd.sep.."familiar morph spider" elseif snd.shamanpet == "spider" then battack = "order spirit passive"..snd.sep.."familiar morph bear" else battack = "order spirit passive"..snd.sep.."familiar morph bear" end else battack = "familiar recall"..snd.sep.."order spirit passive"..snd.sep.."order spirit follow me" end
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end else if wp>=45 and tonumber(gmcp.Char.Vitals.energy) > 3 then battack = battack..snd.sep.."commune boost"..snd.sep.."commune lightning "..snd.bashing.target else battack = battack..snd.sep.."commune lightning "..snd.bashing.target end end end
function snd.shapeshifter_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end else battack = "combo "..snd.bashing.target.." slash slash" end end
function snd.infiltrator_bash() local hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) local endur = 100*(gmcp.Char.Vitals.ep/gmcp.Char.Vitals.maxep) if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif snd.balance.backstab and hasSkill("Backstab") and endur > 75 then if snd.wielding("dirk") then battack = "backstab "..snd.bashing.target else if hasSkill("Quickwield") then battack = "quickwield left dirk"..snd.sep.."backstab "..snd.bashing.target else battack = "wield dirk" end end elseif snd.toggles.newbie then if snd.wielding("whip") then battack = "garrote "..snd.bashing.target else if hasSkill("Quickwield") then battack = "quickwield left whip"..snd.sep.."garrote "..snd.bashing.target else battack = "wield whip" end end else battack = "bite "..snd.bashing.target.." camus" end end
function snd.templar_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp) endur = 100*(gmcp.Char.Vitals.ep/gmcp.Char.Vitals.maxep)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif snd.toggles.newbie then battack = "strikebash "..snd.bashing.target elseif (snd.wielded.left.name:find("warhammer") or snd.wielded.left.name:find("bastard") or snd.wielded.left.name:find("glaive")) and hasSkill("Doubleswing") then battack = "dsw "..snd.bashing.target else battack = "dsk "..snd.bashing.target end if (hp >= 61 and wp >= 50 and hasSkill("Sacrifice")) and not snd.toggles.newbie then battack = battack.. " sacrifice sacrifice" elseif not snd.toggles.newbie and hasSkill("Cleanse") then battack = "cleanse left"..snd.sep.."cleanse right"..snd.sep..battack end end
function snd.teradrim_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) endur = 100*(gmcp.Char.Vitals.ep/gmcp.Char.Vitals.maxep) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "sand shield me" end elseif snd.balance.momentum and hasSkill("Momentum") and endur>=45 then battack = "earth momentum "..snd.sep.."earth batter "..snd.bashing.target..snd.sep.."golem recover" else battack = "earth batter "..snd.bashing.target..snd.sep.."golem recover" end end
function snd.tidesage_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) endur = 100*(gmcp.Char.Vitals.ep/gmcp.Char.Vitals.maxep) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "fog harden me" end elseif snd.balance.momentum and hasSkill("Hightide") and endur>=45 then battack = "tide hightide "..snd.sep.."tide clobber "..snd.bashing.target..snd.sep.."synth recuperate" else battack = "tide clobber "..snd.bashing.target..snd.sep.."synth recuperate" end end
function snd.warden_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp)
