Trigger.script:
local stable_check = { Enorian = "enorianstable", Spinesreach = "spinesreachstable", Bloodloch = "bloodlochstable", Duiran = "duiranstable" }
snd.send("qdmount"..snd.sep.."return mount "..stable_check[gmcp.Char.Status.city]) if sndNDB.highlightNames then expandAlias("sndb hl") end
snd_toggleSave(true) sndNDB_saveSettings(true)

local glimpse = {["Typhoon"] = "air", ["Inferno"] = "fire", ["Maelstrom"] = "water",}

snd.glimpse = glimpse[matches[2]]
snd.fulcrum_ready = false

--The strength of his sadness is at 0%. --The strength of his happiness is at 0%. --The strength of his surprise is at 0%. --The strength of his anger is at 0%. --The strength of his stress is at 0%. --The strength of his fear is at 0%. --The strength of his disgust is at 0%.
local emotional_info = {
neutral = "neutral", sad = "sadness", happy = "happiness", surprised = "surprise", angry = "anger", stressed = "stress", fearful = "fear", disgusted = "disgust",
}
snd.target_induced = emotional_info[matches[2]]
--The strength of his sadness is at 0%. --The strength of his happiness is at 0%. --The strength of his surprise is at 0%. --The strength of his anger is at 0%. --The strength of his stress is at 0%. --The strength of his fear is at 0%. --The strength of his disgust is at 0%.

if matches[2] == snd.target then snd.used.rebounding = false snd.used.shield = false if matches[1]:find("vulnerable") then snd.onHit2({"Dodge"}, "Wayfarer") end
snd.waiting.queue = false
end disableTrigger("Tenacity Devastate")

if matches[2] == snd.target then local writhe_table = { ["state of transfixion"] = "transfix", ["bindings"] = "web", ["thigh-jawlock"] = "thighlock", ["armpit-jawlock"] = "armpitlock", ["neck-jawlock"] = "necklock", ["entanglement by ropes"] = "ropes", ["tied ropes"] = "bind", ["impalement"] = "impaled", ["grappling"] = "grappled", }
snd.target_cured("writhe_"..writhe_table[matches[3]])
if matches[1]:find("jawlock") then snd.jawlocked = false end end

if matches[2] ~= snd.target and (snd.toggles.attacking or snd.toggles.bashing) and snd.toggles.affcalling then local choices = { ["Templar"] = "defend", ["Revenant"] = "juxtapose", ["Ascendril"] = "cast reflection", ["Shaman"] = "nature barrier", ["Alchemist"] = "botany envelop", ["Teradrim"] = "sand shield", ["Tidesage"] = "fog harden", ["Luminary"] = "angel aura", ["Earthcaller"] = "osso ribcage", ["Zealot"] = "enact cascade",}
if choices[snd.class] ~= nil then send("qeb "..choices[snd.class].." "..matches[2]) end
end

setTriggerStayOpen("venoms missing", 0)
local all_venoms = { "sumac", "xentio", "oleander", "eurypteria", "kalmia", "digitalis", "darkshade", "curare", "epteth", "prefarar", "monkshood", "euphorbia", "colocasia", "oculus", "hepafarin", "camus", "vernalius", "epseth", "larkspur", "slike", "voyria", "delphinium", "vardrax", "loki", "aconite", "selarnia", "gecko", "scytherus", "ouabain" }
local missing_venoms = {}
for venom in pairs(all_venoms) do if not table.contains(snd.my_venoms, all_venoms[venom]) then table.insert(missing_venoms, all_venoms[venom]) end end if #missing_venoms >= 1 then snd.message("You are missing: <red>"..table.concat(missing_venoms, "<white>,<red> ")) snd.send(" ") else snd.message("You have all the venoms!") snd.send(" ") end
